import React from 'react';
import style from './Button.module.css';

export const Button = (props) => {



    const buttonStyle = {
        fontSize: `${props.fontSize}px`,
        border: props.isBorder ? "none" : "1px solid black"
    }

    return (
        <div className={style.button} style={buttonStyle}>{props.text}</div>
    )
}
