import React from 'react';
import style from './Navigation_Button.module.css';

export const Navigation_Button = (props) => {

    const isActive = false;
    const toggleActive = (isActive) => {
        
        isActive = !isActive;

        if (isActive) {
            return null;
        }
        else {
            return <div className={style.navButton_active}></div>;
        }
        
    }
    return (
        <div className={style.navButton}  onClick={toggleActive}>
            <p>{props.text}</p>
            <div>{toggleActive}</div>
        </div>
    )
}
