import React from 'react';
import style from './Navigation_Button.module.css';

export const Navigation_Button = (props) => {

    let isActive = false;
    
    const toggleActive = (isActive) => {
        isActive = !isActive;
    }

    return (
        <div className={style.navButton}  onClick={toggleActive}>
            <p>{props.text}</p>
            <div>{(isActive) => {if (isActive) {return <div className={style.navButton_active}></div>} else return null}}</div>
        </div>
    )
}
