import React from 'react';
import style from './Navigation_Button.module.css';

export const Navigation_Button = (props) => {

    const isActive = false;
    const navButton = null;
    const toggleActive = (isActive) => {
        if (isActive) {
            navButton_active = null;
        }
        else {
            navButton_active = <div className={style.navButton_active}></div>;
        }
        isActive = !isActive;
    }
    return (
        <div className={style.navButton}>
            <p>{props.text}</p>
            <div onClick={toggleActive}>{navButton_active}</div>
        </div>
    )
}
